    wb_bcq = openpyxl.load_workbook(filepath_bcq)
    sheet_bcq = wb_bcq.active

    # Create lists to store the data
    hours = []
    scpc_values = []
    kspc1_values = []
    kspc2_values = []
    edc_values = []
    pedc_values = []

